import React, { useState } from "react";
import "./style.css";
import Form from 'react-bootstrap/Form'

// Using the datalist element we can create autofill suggestions based on the props.breeds array
function SearchForm(props) {
  console.log(props)

  const [searchEmployee, setSearchEmployee] = useState("");

  function employeeSearch(evt) {
    setSearchEmployee(evt);
    filterEmployee(searchEmployee, props.employees);
  }

  function filterEmployee(evt, list) {

    const filteredList = list.filter(employee => employee.name.first.includes(evt));

    if (filteredList.length > 0) {
      props.employees(filteredList);

    }

  }

  return (
    <Form.Group>
      <Form.Label>Employee Name:</Form.Label>
      <Form.Control
        type="text"
        placeholder="Type employee name here..."
        name="searchEmployee"
        onChange={evt => employeeSearch(evt.target.value)}
      />
      <datalist id="employees">
        {
          props.employees.map(employee => (
            <option value={`${employee.name.first} ${employee.name.last}`}
              key={`${employee.name.first} ${employee.name.last}`} />
          ))
        }
      </datalist>
    </Form.Group>
  );
}

export default SearchForm;
